mapper.select:
SELECT id, name, category FROM products
AND category = #{category}
AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))

SELECT id, name, category, default_image AS defaultImage, min_price AS minPrice FROM products WHERE status = 'ACTIVE'
AND category = #{category}
AND name LIKE CONCAT('%', #{keyword}, '%')
AND min_price >= #{minPrice}
AND min_price <= #{maxPrice}

mapper.delete:
DELETE FROM product_variants
WHERE id IN

    #{variantId}

mapper.update:
UPDATE product_variants SET product_id = CASE id
WHEN #{variant.id} THEN #{variant.productId}
ELSE product_id END, color = CASE id
WHEN #{variant.id} THEN #{variant.color}
ELSE color END, size = CASE id
WHEN #{variant.id} THEN #{variant.size}
ELSE size END, price = CASE id
WHEN #{variant.id} THEN #{variant.price}
ELSE price END, stock_quantity = CASE id
WHEN #{variant.id} THEN #{variant.stockQuantity}
ELSE stock_quantity END, image = CASE id
WHEN #{variant.id} THEN #{variant.image}
ELSE image END WHERE id IN
#{variant.id}

UPDATE product_variants
SET image = NULL
WHERE product_id = #{productId}
AND color IN

    #{color}
